import React from 'react'
import JsonData from '../contract.json'
import "./CSS/Table.css"
import ReactDOM from 'react-dom'

const basePath = 'https://gist.githubusercontent.com/adielhercules/de4b9a8da2c85a172a80a74af5d868c0/raw/871d0f2442489ffc3d814fce858b14f2d4cb3892/';
const gistJson = file => `${basePath}${file}.json`;

const firstFile = gistJson('first-set');
const secondFile = gistJson('second-set');
const fakeFiles = [firstFile, secondFile];
const requestOpt = {
  method: 'GET',
  headers: { 'Content-Type': 'application/json' },
}


const fetchFakeJson = async () => {

  const response = await fetch('http://127.0.0.1:5000/getContracts', requestOpt)
    .then(response => response.json())
    .catch(error => console.log(error));

  const json = await response.json();

  return json;
}


class DevContracts extends React.Component {
  state = {
    sortBy: null,
    sortDirection: true,
    data: [],
    isLoading: true,
    error: null,
  }

  async componentDidMount() {
    const data = await fetchFakeJson();

    this.setState({ data, isLoading: false });
  }

  reset = () => {
    this.setState({ isLoading: true, error: null });

    window.scrollTo(0, 0);

    this.textbox.value = '';

    fetchFakeJson().then(data => {
      this.setState({ data, isLoading: false });
    })
  }

  loadCustomData = () => {
    const jsonText = String(this.textbox.value).trim() || '[]';;
    let json;

    try {
      json = JSON.parse(jsonText);

      if (json.length > 0) {
        this.setState({ data: json });
      } else {
        this.setState({ error: 'Empty collection of data detected' });
      }
    } catch (error) {
      this.setState({ error: 'Invalid data. Please validate your data first.' });
    }
  }

  getHeaders = data => {
    const headers = data.reduce((headers, item) => {
      const _headers = Object.keys(item).reduce((_headers, header) => ({ ..._headers, [header]: null }), {});

      return {
        ...headers,
        ..._headers,
      };
    }, {});


    return Object.keys(headers);
  };

  fillCells = headers => item => Object.assign({}, headers.reduce((_headers, header) => ({ ..._headers, [header]: null }), {}), item);

  sortBy = sortBy => e => {
    e.preventDefault();

    this.setState(state => ({ sortBy, sortDirection: state.sortBy === sortBy ? !state.sortDirection : true }));
  }

  sortDataBy = (sortBy, direction) => (a, b) => {
    if (direction) {
      return a[sortBy] > b[sortBy] ? 1 : (a[sortBy] < b[sortBy] ? -1 : 0);
    }

    return a[sortBy] > b[sortBy] ? -1 : a[sortBy] < b[sortBy] ? 1 : 0;
  };

  render() {
    const { data, sortBy, sortDirection, isLoading, error } = this.state;

    const headers = this.getHeaders(data);

    return (
      <div>
        <div class="responsive-table">
          <div id="dynamic-table">
            <table id="sortable" style={{ display: isLoading ? 'none' : 'inherit' }}>
              <thead>
                <tr>
                  {headers.map(header => (
                    <th key={header} className={sortBy === header ? sortDirection ? 'ascendent_sort' : 'descendent_sort' : ''}><a href="javascript:;" onClick={this.sortBy(header)}>{header.toUpperCase()}</a></th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {data.sort(this.sortDataBy(sortBy, sortDirection)).map(this.fillCells(headers)).map((item, rowIndex) => (
                  <tr key={rowIndex}>
                    {Object.values(item).map((value, cellIndex) => (
                      <td key={`${rowIndex}.${cellIndex}`}>{value}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        <button class="btn" onClick={this.reset}>Reset Table (random data)</button>{' '}
        <button class="btn" onClick={this.loadCustomData}>Load custom data</button>

        <div class="custom-data">
          {error && <p className="error">{error}</p>}
          <textarea name="customData" id="textbox" ref={el => { this.textbox = el }} cols="30" rows="10"></textarea>
        </div>
      </div>
    );
  }
}

// const dynamicTable = document.getElementById('root');
// ReactDOM.render(<App />, dynamicTable);
export default DevContracts;
