import React from 'react'
import JsonData from '../contract.json'
import "./CSS/Table.css"

function ProductTable(props) {
  
  const requestOpt = {
    method: 'GET',
    headers: { 'Content-Type': 'application/json' },
  }
  async function fetchFunc() {
    return await fetch('http://127.0.0.1:5000/getContracts', requestOpt)
      .then(response => response.json())
      .catch(error => console.log(error));
  }
  (async () => {
    products = await fetchFunc();
  })()

  const { products } = props;
  
  const [sortConfig, setSortedField] = React.useState(null);
  let sortedProducts = [...products];
  if (sortConfig !== null) {
    sortedProducts.sort((a, b) => {
      if (a[sortConfig.key] < b[sortConfig.key]) {
        return sortConfig.direction === 'ascending' ? -1 : 1;
      }
      if (a[sortConfig.key] > b[sortConfig.key]) {
        return sortConfig.direction === 'ascending' ? 1 : -1;
      }
      return 0;
    });
  }
  return (
    <table>
      <caption>Job Listings</caption>
      <thead>
        <tr>
          <th>
            <button type="button" onClick={() => setSortedField('contract_name')}>
              Contract Name
            </button>
          </th>
          <th>
            <button type="button" onClick={() => setSortedField('contract_length')}>
              Contract Length
            </button>
          </th>
          <th>
            <button type="button" onClick={() => setSortedField('open')}>
              Open
            </button>
          </th>
        </tr>
      </thead>
      <thead>
        <tr>
          <th>ID</th>
          <th>ComID</th>
          <th>ConName</th>
          <th>ConLen</th>
          <th>ConVal</th>
          <th>ConDes</th>
          <th>ProLan</th>
          <th>Loc</th>
          <th>Open</th>
          <th>Date</th>
        </tr>
      </thead>
      <tbody>
        {products.map(product => (
          <tr key={product.id}>
            <td>{product.contract_name}</td>
            <td>{product.contract_length}</td>
            <td>{product.contract_value}</td>
            <td>{product.description}</td>
            <td>{product.progamming_language}</td>
            <td>{product.location}</td>
            <td>{product.open}</td>
            <td>{product.date}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}


export default ProductTable;
