import React, { useState } from 'react'
import './CSS/LoginCSS.css'
import ReactDOM from 'react-dom'
import logo from '../DevCo.gif'
import { Navigate } from 'react-router-dom'
import ComReg from './Com_Registration'
import DevReg from './Dev_Registration'

const ImgUpload = ({
    onChange,
    src
}) =>

    <div  >
        <img htmlFor="photo-upload" className='loginimg' src={src} alt="DEVCO LOGO" />
    </div>

const Email = ({
    onChange,
    value
}) =>
    <div className="field">
        <label htmlFor="email">
            Developer Email / Company Name:
        </label>
        <input
            id="email"
            type="text"
            onChange={onChange}
            maxlength="25"
            value={value}
            placeholder="jbloggs@mail.com"
            required />
    </div>

const Password = ({
    onChange,
    value
}) =>
    <div className="field">
        <label htmlFor="password">
            Password:
        </label>
        <input
            id="password"
            type="password"
            onChange={onChange}
            maxlength="25"
            value={value}
            placeholder="password"
            required />
    </div>

const handleDevReg = () => {
    window.location.pathname = "/DevReg";
};

const handleComReg = () => {
    window.location.pathname = "/ComReg";
};

const Edit = ({
    onSubmit,
    children,
}) =>
    <div className="card">
        <form onSubmit={onSubmit}>
            {children}
            <button type="submit" className="login">LOGIN </button>
            <div>
                <button className="DRegBtn" onClick={handleDevReg}>Developer Registration</button>
                <button className="CRegBtn" onClick={handleComReg}>Company Registration</button>
            </div>

        </form>
    </div>

const Profile = ({
    onSubmit,
    email,
    password,
}) =>
    <div className="card">
        <form onSubmit={onSubmit}>
            <h1>Successfully Logged in</h1>
            <div className="email">{email}</div>
            <div className="password">{password}</div>

        </form>
    </div>


class Login extends React.Component {
    state = {
        email: '',
        password: '',
        active: 'edit',
    }

    editEmail = e => {
        const email = e.target.value;
        this.setState({
            email,
        });
    }

    editPassword = e => {
        const password = e.target.value;
        this.setState({
            password,
        });
    }

    handleSubmit = e => {
        e.preventDefault();


        let data = this.state;
        const requestOpt = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                'email': data.email,
                'password': data.password,
            }),
        }
        async function fetchFunc() {
            return await fetch('http://127.0.0.1:5000/login', requestOpt)
                .then(response => response.json())
                .catch(error => console.log(error));
        }
        (async () => {
            let info = await fetchFunc();
            if (info.success) { // correct login info
                // let activeP = data.active === 'edit' ? 'profile' : 'edit';
                // this.setState({
                //     active: activeP,
                // })
                <Redirect to="/" />
                localStorage.setItem("isAuthenticated", "true");
                window.location.pathname = "/";

            } else {
                alert(info.msg);

            }
        })()
    }

    render() {
        const {
            email,
            password,
            active } = this.state;

        return (
            <div>

                {(active === 'edit') ? (

                    <Edit onSubmit={this.handleSubmit}>
                        <ImgUpload src={logo} className="loginimg" />
                        <Email onChange={this.editEmail} value={email} />
                        <Password onChange={this.editPassword} value={password} />
                    </Edit>


                ) : (
                    <Profile
                        onSubmit={this.handleSubmit}
                        email={email}
                        password={password}
                    />)}

            </div>
        )
    }
}
// ReactDOM.render(
//     <>
//         <Login />
//     </>
//     ,
//     document.getElementById('root')
// )

export default Login;
